#!/usr/bin/env python3
# -*- coding: utf-8 -*-

import base64, struct
bmp_data = base64.b64decode('')

# BMP格式采用小端方式存储数据
def bmp_info(data):
	s = struct.unpack('<ccIIIIIIHH', data[:30])
	print(s[0:2])
	return {'width': s[-4], 'height': s[-3], 'color': s[-1] }

# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')